import { useEffect } from "react"
import { useDispatch, useSelector } from "react-redux"
import { changeOrder } from "../reducer/actions"

export default function Order(){
    const {pokemons, order} = useSelector((state) => state)
    const dispatch = useDispatch()

    useEffect(() => {
        
        console.log(order(order, pokemons))
        //dispatch(changeOrder(order, ))
    }, [])

    function handleChange(e){
        console.log(e.target.value)
    }
    return(<>
        <label for="Ordenar">Ordenar</label>
        <select name="Ordenar" onChange={handleChange}>
            <option selected value="A - Z">A - Z</option>
            <option value="Z - A">Z - A</option>
        </select>
        </>
    )
} 

function order(order, pokemons){
    switch(order){
        case 'A - Z': 
            return pokemons.sort((a,b) => {
                if(a.name > b.name) return 1;
                if(a.name < b.name) return -1;
                return 0
            })
    }
}
