import {GET_DETAILS, GET_POKEMONS, GET_TYPES, CLEAR_DETAILS, CHANGE_PAGE} from "./actions"

const initialState = {
    pokemons: [],
    details: {},
    types: [],
    page: 1
}

function rootReducer(state=initialState, action){
    switch(action.type){
        case GET_POKEMONS:
            return {
                ...state, 
                pokemons: action.payload
            }
        case GET_DETAILS:
            return {
                ...state, 
                details: action.payload
            }
        case CLEAR_DETAILS:
            return {
                ...state, 
                details: action.payload
            }
        case GET_TYPES:
            return {
                ...state, 
                types: action.payload
            }
        case CHANGE_PAGE:
            return {
                ...state, 
                page: action.payload
            }
        default:
            return state
    }
}
export default rootReducer;
