import { useEffect, useState } from "react"
import { useDispatch, useSelector } from "react-redux"
import { getDetails, clearDetails } from "../reducer/actions"

export default function Details(props){
    const d = useSelector((state) => state.details)
    const dispatch = useDispatch()
    
    useEffect(() => {
        if(d.id !== props.id) {
            dispatch(getDetails(props.id))
        }
        return () => {dispatch(clearDetails())}
    }, [])

    return(
        !Object.keys(d).length ? <h1>cargando...</h1> : 
        <div className="Details">
            <h1>{d.id}</h1>
            <h1>{d.name}</h1>
            <img src={d.image} />
            {d.types?.map(t => <p key={d.id}>{t.type.name}</p>)}
            <p>HP: {d.hp}</p>
            <p>Attack: {d.attack}</p>
            <p>Defense: {d.defense}</p>
            <p>Speed: {d.speed}</p>
            <p>Height: {d.height}</p>
            <p>Weight: {d.weight}</p>
        </div>
    )
}
