import { useDispatch, useSelector } from 'react-redux';
import { NavLink } from 'react-router-dom';
import { changeOrder, changeFilter, changePage } from '../reducer/actions';
import Loading from './Loading';
import './styles/Cards.css';

export default function Cards(){
    const pokemons = useSelector((state) => state.pokemons)
    const page = useSelector((state) => state.page)
    const dispatch = useDispatch()

    const pokeGroup = pokemons.slice((page-1)*12, page*12)

    function handlePage(pag){
        if (page === pag) return;
        if (pag > 4 || pag < 1) return;
        dispatch(changePage(pag))
    }
    function handleFilter(filter){
        dispatch(changeFilter(filter))
    }
    function handleOrder(filter){
        dispatch(changeOrder(filter)) 
    }
    return(
        !pokemons.length ? <Loading/> : <>
            <div className='cards'>
                {pokeGroup.map(p => {
                    return <NavLink to={`/home/${p.id}`} key={p.id}>
                        <div className='card'>
                            <span>{p.name}</span>
                            <img src={p.image}/>
                            {p.types.map(t => <p>{t.type.name}</p>)}
                        </div>
                    </NavLink>
                })}
            </div>  
            <div className='pages'>
                <button onClick={() => {handlePage(page-1)}}>{'<'}</button>
                <button onClick={() => {handlePage(1)}}>1</button>
                <button onClick={() => {handlePage(2)}}>2</button>
                <button onClick={() => {handlePage(3)}}>3</button>
                <button onClick={() => {handlePage(4)}}>4</button>
                <button onClick={() => {handlePage(page+1)}}>{'>'}</button>
            </div>
        </>
    )
}
