import {GET_POKEMONS} from "./actions"

const initialState = {
    pokemons: [],
    page: 1
}

function rootReducer(state=initialState, action){
    switch(action.type){
        case GET_POKEMONS:
            return {
                ...state, 
                pokemons: action.payload.data.slice(0, action.payload.page*12)
            }
        default:
            return state
    }
}
export default rootReducer;
