import axios from 'axios';

export const GET_POKEMONS = 'GET_POKEMONS'

export function getPokemons(page){
    return (dispatch) => {
        return fetch('http://localhost:3001/pokemons')
        .then(r => r.json())
        .then(r => {
            dispatch({type: GET_POKEMONS, payload: {r, page}})
        })    
    }
}
