import { useDispatch, useSelector } from 'react-redux';
import { NavLink } from 'react-router-dom';
import { changeOrder, changeFilter, changePage } from '../reducer/actions';
import Filter from './Filter';
import Loading from './Loading';
import Order from './Order';
import './styles/Cards.css';

export default function Cards(){
    const {pokemons,page,order,types,filters} = useSelector((state) => state)
    const dispatch = useDispatch()

    const ordered = orderMachine(order, pokemons, types)
    const filtered = filterMachine(filters, ordered)    

    const pokeGroup = order !== 'Por Tipo' ? filtered.slice((page-1)*12, page*12) 
    : filtered

    function handlePage(pag){
        if (page === pag) return;
        if (pag > Math.ceil(pokemons.length/12) || pag < 1) return;
        dispatch(changePage(pag))
    }
    return(
        !pokemons.length ? <Loading/> : <>
            <Filter/>
            <Order/>
            {order !== 'Por Tipo' ?
                <div className='cards'>
                    {pokeGroup.map(p => 
                        <NavLink to={`/home/${p.id}`} key={p.id}>
                            <div className='card'>
                                <span>{p.name}</span>
                                {p.image ? <img src={p.image}/> : <p style={{fontSize: '48px'}}>?</p>}
                                {p.types?.map(t => <p key={t.type.name}>{t.type.name}</p>)}
                            </div>
                        </NavLink>
                    )}
                </div>
                : 
                Object.keys(pokeGroup).map(t => {if (pokeGroup[t].length) return <reactFragment key={t}>
                    <h2>{t}</h2>
                    <br />
                    <div className='cards'>
                        {pokeGroup[t].map(p => 
                            <NavLink to={`/home/${p.id}`} key={p.id}>
                                <div className='card'>
                                    <span>{p.name}</span>
                                    <img src={p.image}/>
                                    {p.types.map(t => <p>{t.type.name}</p>)}
                                </div>
                            </NavLink>)}
                    </div>
                    </reactFragment>
                    else return}
                )
            }
            <div className='pages'>
                <button onClick={() => {handlePage(page-1)}}>{'<'}</button>
                {newOrdArray(Math.ceil(pokemons.length/12)).map(p => 
                    <button onClick={() => {handlePage(p)}}>{p}</button>)}
                <button onClick={() => {handlePage(page+1)}}>{'>'}</button>
            </div>
        </>
    )
}

function orderMachine(order, pokemons, types){
    switch(order){
        case 'A - Z': 
        return pokemons.sort((a,b) => a.name.localeCompare(b.name))

        case 'Z - A': 
        return pokemons.sort((a,b) => b.name.localeCompare(a.name))

        case 'Por Tipo': 
            const ty = types.sort()
            const ordType = ty.map(type => 
                [type, 
                pokemons.filter(p => p.types.find(t => t.type.name === type))]
            )
        return Object.fromEntries(ordType) 

        case 'Mas fuerte': 
        return pokemons.sort((a,b) => b.attack-a.attack)

        case 'Mas defensivo': 
        return pokemons.sort((a,b) => b.defense-a.defense)
        
        case 'Mas rapido': 
        return pokemons.sort((a,b) => b.speed-a.speed)
        
        default: return pokemons
    }
}

function filterMachine(filters, pokemons){
    const pokeGroup = pokemons
    filters.typeFilter.forEach(element => {
        pokeGroup = pokeGroup.filter(p => 
            p.types.map(t => t.type.name).find(x => x === element))
    })
    return pokeGroup
}

function newOrdArray(n){
    let arr = []
    for(var i = 1; i <= n; i++){
        arr.push(i)
    }
    return arr
}
