import { useState } from "react";
import { useDispatch } from "react-redux";
import { useHistory } from 'react-router'
import { Link } from "react-router-dom";
import { getPokemonByName, toogleMenu } from "../reducer/actions";
import PikaBossSvg from './pikaBossSVG';
import pikaBossLogo from '../multimedia/pikaboss.png'
import './styles/Nav.css';

export default function Nav(){
    const dispatch = useDispatch() 
    
    function handleMenu(e){
        dispatch(toogleMenu())
    }

    return (
        <div className='nav'>
            <button className='menu' onClick={handleMenu}></button>
            <Link exact to='/home'>
                <div className='titleHome'>
                    <img src={pikaBossLogo} className='pikaBossImg'/>
                    <div className='titleH'>PikaBoss</div>
                </div>
            </Link>
            <svg style={{position: 'absolute', height: 0, width: 0}}>
                <filter id="blue-glow">
                    <feFlood result="flood" flood-color="blue" flood-opacity="1"></feFlood>
                    <feComposite in="flood" result="mask" in2="SourceGraphic" operator="in"></feComposite>
                    <feMorphology in="mask" result="dilated" operator="dilate" radius="2"></feMorphology>
                    <feGaussianBlur in="dilated" result="blurred" stdDeviation="5"></feGaussianBlur>
                    <feMerge>
                        <feMergeNode in="blurred"></feMergeNode>
                        <feMergeNode in="SourceGraphic"></feMergeNode>
                    </feMerge>
                </filter>
            </svg>
            <svg style={{display: 'none', height: 0, width: 0}}>
                <filter id="self-color-glow">
                    <feFlood result="flood" flood-color="blue" flood-opacity="1"></feFlood>
                    <feComposite in="flood" result="mask" in2="SourceGraphic" operator="in"></feComposite>
                    <feMorphology in="mask" result="dilated" operator="dilate" radius="2"></feMorphology>
                    <feGaussianBlur in="dilated" result="blurred" stdDeviation="5"></feGaussianBlur>
                    <feMerge>
                        <feMergeNode in="blurred"></feMergeNode>
                        <feMergeNode in="SourceGraphic"></feMergeNode>
                    </feMerge>
                </filter>
            </svg>
            <SearchBar class='searchBar'/>
            <div className='create'>
            <Link exact to='/create'>
                <button className='createBtn'><p id='txtCrear'>+ Crear Pokemon</p></button>
            </Link>
            </div>
        </div>
    )
}
export function SearchBar(props){
    const [input, setInput] = useState('')
    const history = useHistory()

    function handleChange(e){
        setInput(e.target.value)
    }

    function handleSubmit(e){
        e.preventDefault()
        history.replace('/home/'+input)
    }
    return (
        <form className={props.class} onSubmit={handleSubmit}>
                <div className='borderfocus'>
                    <input type='text' placeholder='Buscar Pokemon...'
                    value={input} onChange={handleChange}
                    data-testid='searchBar'/>
                </div>
                <button type='submit' className='searchBtn'>Buscar</button>
            </form>
    )
}
