import { useState } from "react";
import { useDispatch } from "react-redux";
import { useHistory } from 'react-router'
import { NavLink } from "react-router-dom";
import { getPokemonByName } from "../reducer/actions";
import PikaBossSvg from './pikaBossSVG';
import './styles/Nav.css';

export default function Nav(){
    const [input, setInput] = useState('')
    const history = useHistory()

    function handleChange(e){
        setInput(e.target.value)
    }

    function handleSubmit(e){
        e.preventDefault()
        history.replace('/home/'+input)
    }

    return (
        <div className='nav'>
            <NavLink exact to='/home'>
                <div className='titleHome'>
                    <PikaBossSvg className='pikaBossSvg'/>
                    <h1 className='titleH'>PikaBoss</h1>
                </div>
            </NavLink>
            <form className='searchBar' onSubmit={handleSubmit}>
                <input type='text' placeholder='Buscar Pokemon...'
                value={input} onChange={handleChange}/>
                <button type='submit'>Buscar</button>
            </form>
            <NavLink exact to='/create'><button className='create'>+ Crear Pokemon</button></NavLink>
        </div>
    )
}
