import { useEffect, useState } from "react"
import { useDispatch } from "react-redux"
import { useSelector } from "react-redux"

export default function Filter(){
    const {types} = useSelector((state) => state)
    const dispatch = useDispatch()
    const [checked, setChecked] = useState({
        bug: false, dark: false, dragon: false, electric: false, fairy: false, 
        fighting: false, fire: false, flying: false, ghost: false, grass: false, 
        ground: false, ice: false, normal: false, poison: false, psychic: false, 
        rock: false, shadow: false, steel: false, unknown: false, water: false, 
    })

    useEffect(() => {
        const typeFilter = Object.entries(checked)
        .filter(([k, v]) => v).map(([k, v]) => k)
        console.log(typeFilter)
        //dispatch(changeFilter([checked.filter(ch => )]))
    })

    function handleTypesChange(type){
        console.log(type)
        setChecked({...checked, [type]: !checked[type]})
    }
    return(
        <div className="filters">
            <div className='typeFilter'>
                {types.map(t => <label><input key={t} type="checkbox" value={checked[t]} checked={checked[t]} onChange={() => {handleTypesChange(t)}}/>{t}</label>)}
            </div>
        </div>
    )
}
