import {GET_POKEMONS} from "./actions"
import { getPokemons } from "./actions";

const initialState = {
    everyPokemons: [],
    page: 1
}

function reducer(state=initialState, action){
    switch(action.type){
        case GET_POKEMONS:
            return {
                ...state, 
                pokemons: action.payload.data.slice(0, action.payload.page*12)
            }
    }
}
export default reducer;
