import {GET_POKEMONS} from "./actions"

function rootReducer(state, action){
    switch(action.type){
        case GET_POKEMONS:
            return {
                ...state, 
                pokemons: action.payload.data.slice(0, action.payload.page*12)
            }
    }
}
export default rootReducer;
