import { useDispatch, useSelector } from 'react-redux';
import { NavLink } from 'react-router-dom';
import { changeOrder, changeFilter, changePage } from '../reducer/actions';
import Loading from './Loading';
import Order from './Order';
import './styles/Cards.css';

export default function Cards(){
    const {pokemons,page,order,types} = useSelector((state) => state)
    const dispatch = useDispatch()

    const ordered = orderMachine(order, pokemons, types)

    const pokeGroup = order !== 'Por Tipo' ? ordered.slice((page-1)*12, page*12) 
    : ordered

    function handlePage(pag){
        if (page === pag) return;
        if (pag > 4 || pag < 1) return;
        dispatch(changePage(pag))
    }
    function handleFilter(filter){
        dispatch(changeFilter(filter))
    }
    return(
        !pokemons.length ? <Loading/> : <>
            <Order/>
                {order !== 'Por Tipo' ?
                    <div className='cards'>
                        {pokeGroup.map(p => 
                            <NavLink to={`/home/${p.id}`} key={p.id}>
                                <div className='card'>
                                    <span>{p.name}</span>
                                    {p.image ? <img src={p.image}/> : <span style='font-size: 48px'>?</span>}
                                    {p.types?.map(t => <p key={t.type.name}>{t.type.name}</p>)}
                                </div>
                            </NavLink>
                        )}
                    </div>
                    : 
                    Object.keys(pokeGroup).map(t => {if (pokeGroup[t].length) return <>
                        <h2>{t}</h2>
                        <br />
                        <div className='cards'>
                            {pokeGroup[t].map(p => 
                                <NavLink to={`/home/${p.id}`} key={p.id}>
                                    <div className='card'>
                                        <span>{p.name}</span>
                                        <img src={p.image}/>
                                        {p.types.map(t => <p>{t.type.name}</p>)}
                                    </div>
                                </NavLink>)}
                        </div>
                        </>
                        else return}
                    )
                }
            <div className='pages'>
                <button onClick={() => {handlePage(page-1)}}>{'<'}</button>
                <button onClick={() => {handlePage(1)}}>1</button>
                <button onClick={() => {handlePage(2)}}>2</button>
                <button onClick={() => {handlePage(3)}}>3</button>
                <button onClick={() => {handlePage(4)}}>4</button>
                <button onClick={() => {handlePage(page+1)}}>{'>'}</button>
            </div>
        </>
    )
}

function orderMachine(order, pokemons, types){
    switch(order){
        case 'A - Z': 
        return pokemons.sort((a,b) => a.name.localeCompare(b.name))

        case 'Z - A': 
        return pokemons.sort((a,b) => b.name.localeCompare(a.name))

        case 'Por Tipo': 
            const ty = types.sort()
            const ordType = ty.map(type => 
                [type, 
                pokemons.filter(p => p.types.find(t => t.type.name === type))]
            )
        return Object.fromEntries(ordType) 

        /* case 'Mas fuerte': 
        return pokemons.sort((a,b) => a.attack.localeCompare(b.attack))

        case 'Mas defensivo': 
        return pokemons.sort((a,b) => a.defense.localeCompare(b.defense))
        
        case 'Mas rapido': 
        return pokemons.sort((a,b) => a.speed.localeCompare(b.speed))

        case 'Mas rapido': 
        return pokemons.sort((a,b) => a.speed.localeCompare(b.speed)) */
        
        default: return pokemons
    }
}
