import { useEffect } from "react"
import { useDispatch, useSelector } from "react-redux"
import { changeOrder } from "../reducer/actions"

export default function Order(){
    const {pokemons, order} = useSelector((state) => state)
    const dispatch = useDispatch()
    
    useEffect(() => {     
        
    }, [])

    function orderMachine(order, pokemons){
        switch(order){
            case 'A - Z': 
                console.log('here')
                return pokemons.sort((a,b) => a.name.localeCompare(b.name))
        }
    }
    
    function handleChange(e){
        const ordered = orderMachine(e.target.value, pokemons)
        dispatch(changeOrder(e.target.value, ordered))
    }
    return(<>
        <label for="Ordenar">Ordenar</label>
        <select name="Ordenar" onChange={handleChange}>
            <option selected value="A - Z">A - Z</option>
            <option value="Z - A">Z - A</option>
        </select>
        </>
    )
}
