import { useEffect } from "react"
import { useDispatch, useSelector } from "react-redux"
import { getDetails, clearDetails, getPokemonByName } from "../reducer/actions"
import Loading from "./Loading"
import NotFound from "./NotFound"
import './styles/details.css'

export default function Details(props){
    const d = useSelector((state) => state.details)
    const dispatch = useDispatch()
    
    useEffect(() => {
        if(parseInt(props.id) || props.id.length > 20){
            dispatch(getDetails(props.id))
        }else{
            dispatch(getPokemonByName(props.id))
        }
        return () => {dispatch(clearDetails())}
    }, [])

    return(<>
        {console.log(d)}
        {!Object.keys(d).length ? <Loading/> : 
        d.name === 'Error' ? <NotFound/> :
        <div className="Details">
            <h1>{capitalize(d.name)}</h1>
            <img src={d.image} />
            {d.types?.map(t => {
                return (
                    <div key={t} className='detType'>
                        <img className='detTypeImg' src={process.env.PUBLIC_URL + `/typesLogos/${t}.png`}/>
                        <p >{capitalize(t)}</p>
                    </div>
                )
            })}
            <div>HP: 
                <div className='stat' style={{width: `${100/maxStats.hp*d.hp}`, height:'5px', backgroundColor: 'green'}}></div>
                {d.hp}
                {console.log(`${100/maxStats.hp*d.hp}`)}
            </div>
            <div>Attack: 
                <div className='stat' style={{width: `${100/maxStats.attack*d.attack}`}}></div>
                {d.attack}
            </div>
            <div>Defense: 
                <div className='stat' style={{width: `${100/maxStats.defense*d.defense}`}}></div>
                {d.defense}
            </div>
            <div>speed: 
                <div className='stat' style={{width: `${100/maxStats.speed*d.speed}`}}></div>
                {d.speed}
            </div>

            <div>Height: {d.height}</div>
            <div>Weight: {d.weight}</div>
            <div>ID: {d.id}</div>
        </div>}
        </>
    )
}
const capitalize = (str) => str[0].toUpperCase()+str.slice(1) 

const maxStats = {
    hp: 255,
    attack: 181,
    defense: 230,
    speed: 160
}
