import { useEffect } from "react"
import { useDispatch, useSelector } from "react-redux"
import { getDetails, clearDetails, getPokemonByName, deletePokemon } from "../reducer/actions"
import Loading from "./Loading"
import NotFound, { ReturnBtn } from "./NotFound"
import './styles/details.css'

export default function Details(props){
    const d = useSelector((state) => state.details)
    const dispatch = useDispatch()
    
    useEffect(() => {
        if(parseInt(props.id) || props.id.length > 20){
            dispatch(getDetails(props.id))
        }else{
            dispatch(getPokemonByName(props.id))
        }
        return () => {dispatch(clearDetails())}
    }, [])

    function handleDelete(){
        dispatch(deletePokemon()) 
    }

    return(<>
        {console.log(d)}
        {!Object.keys(d).length ? <Loading/> : 
        d.name === 'Error' ? <NotFound/> :
        <div className="Details">
            <ReturnBtn/>
            <h1>{capitalize(d.name)}</h1>
            <div className="detAndImgContainer">
                <img className='primaryImg' src={d.image} />
                <div className='detContainer'>
                    <div className="statBars">
                        <div className="statBar">HP: {d.hp}
                            <div className='bgStat'>
                                <div className='stat' style={{width: `${Math.round(100/maxStats.hp*d.hp)}%`}}></div>
                            </div>
                        </div>
                        <div className="statBar">Attack: {d.attack}
                            <div className='bgStat'>
                                <div className='stat' style={{width: `${Math.round(100/maxStats.attack*d.attack)}%`}}></div>
                            </div>
                        </div>
                        <div className="statBar">Defense: {d.defense}
                            <div className='bgStat'>
                                <div className='stat' style={{width: `${Math.round(100/maxStats.defense*d.defense)}%`}}></div>
                            </div>
                        </div>
                        <div className="statBar">speed: {d.speed}
                            <div className='bgStat'>
                                <div className='stat' style={{width: `${Math.round(100/maxStats.speed*d.speed)}%`}}></div>
                            </div>
                        </div>
                    </div>
                    <div className='extraStats'>
                        <div>Height: {d.height}</div>
                        <div>Weight: {d.weight}</div>
                        <div>ID: {d.id}</div>
                    </div>
                    <div className='detTypesContainer'>
                        {d.types?.map(t => {
                            return (
                                <div key={t} className='detType'>
                                    <img className='detTypeImg' src={process.env.PUBLIC_URL + `/typesLogos/${t}.png`}/>
                                    <p >{capitalize(t)}</p>
                                </div>
                            )
                        })}
                    </div>
                </div>
                {d.origin === 'My' && <button className='deleteBtn' onClick={handleDelete}>Eliminar Pokemon</button>}
            </div>
            <svg style={{position: 'absolute', height: 0, width: 0}}>
                <filter id="blue-glow">
                    <feFlood result="flood" flood-color="blue" flood-opacity="1"></feFlood>
                    <feComposite in="flood" result="mask" in2="SourceGraphic" operator="in"></feComposite>
                    <feMorphology in="mask" result="dilated" operator="dilate" radius="2"></feMorphology>
                    <feGaussianBlur in="dilated" result="blurred" stdDeviation="5"></feGaussianBlur>
                    <feMerge>
                        <feMergeNode in="blurred"></feMergeNode>
                        <feMergeNode in="SourceGraphic"></feMergeNode>
                    </feMerge>
                </filter>
            </svg>
        </div>}
        </>
    )
}
const capitalize = (str) => str[0].toUpperCase()+str.slice(1) 

const maxStats = {
    hp: 255,
    attack: 181,
    defense: 230,
    speed: 160
}
