import { useSelector } from 'react-redux';
import { NavLink } from 'react-router-dom';
import './styles/Cards.css';

export default function Cards(){
    const pokemons = useSelector((state) => state.pokemons)

    return(
        !pokemons.length ? <h1>cargando...</h1> : 
            <div className='cards'>
                {pokemons.map(p => {
                    return <NavLink to={`/details${p.id}`}>
                        <div className='card'>
                            <span>{p.name}</span>
                            <img src={p.image}/>
                            {p.types.map(t => <p>{t.type.name}</p>)}
                        </div>
                    </NavLink>
                })}
            </div>  
    )
}
