import {GET_DETAILS, GET_POKEMONS} from "./actions"

const initialState = {
    pokemons: [],
    details: {},
    types: [],
    page: 1
}

function rootReducer(state=initialState, action){
    switch(action.type){
        case GET_POKEMONS:
            return {
                ...state, 
                pokemons: action.payload
            }
        case GET_DETAILS:
            console.log(action.payload)
            return {
                ...state, 
                details: action.payload
            }
        default:
            return state
    }
}
export default rootReducer;
