import { useEffect, useState } from "react"
import { useDispatch } from "react-redux"
import { useSelector } from "react-redux"
import { changeFilter } from "../reducer/actions"

export default function Filter(){
    const {types, filter} = useSelector((state) => state)
    const dispatch = useDispatch()
    const [typeFilter, setTypeFilter] = useState([])

    function handleTypesChange(e){
        console.log(e.target.value)
        if(typeFilter.find(x => x === e.target.value)) {
            setTypeFilter(typeFilter.filter(x => x !== e.target.value)) 
        }else{
            setTypeFilter([...typeFilter, e.target.value])
        }
        dispatch(changeFilter({...filter, typeFilter}))
    }
    return(
        <div className="filters">
            {/* <div className='typeFilter'>
                {types.map(t => <label><input key={t} type="checkbox" value={checked[t]} checked={checked[t]} onChange={() => {handleTypesChange(t)}}/>{t}</label>)}
            </div> */}
            {typeFilter.map(t => <button type="button" value={t} onClick={handleTypesChange}>{t}</button>)}
            <select onChange={handleTypesChange}>
                {types.map(t => <option value={t}>{t}</option>)}
            </select>
        </div>
    )
}
